CREATE OR REPLACE FUNCTION obter_grupo_diag_sintoma ( nr_seq_diag_sintoma_p bigint, ie_tipo_retorno_p bigint) RETURNS varchar AS $body$
DECLARE


/*	ie_tipo_retorno_p
		1 - Sintoma
		2 - Diagn√≥stico */
ds_retorno_w		varchar(60);


BEGIN

if (ie_tipo_retorno_p = 1) then
	begin
	select	c.ds_grupo_sintoma
	into STRICT	ds_retorno_w
	from	eme_sintoma b,
		eme_grupo_sintoma c
	where	b.nr_sequencia = nr_seq_diag_sintoma_p
	and	b.nr_seq_grupo = c.nr_sequencia;
	end;
end if;

if (ie_tipo_retorno_p = 2) then
	begin
	select	c.ds_grupo_diag
	into STRICT	ds_retorno_w
	from	eme_diagnostico b,
		eme_grupo_diag c
	where	b.nr_sequencia = nr_seq_diag_sintoma_p
	and	b.nr_seq_grupo  = c.nr_sequencia;
	end;
end if;

RETURN	ds_retorno_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
