CREATE OR REPLACE FUNCTION obter_grupo_exame_imp_estab ( nr_seq_exame_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text, cd_convenio_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_grupo_w		varchar(50);
nr_seq_grupo_imp_w	bigint;
ds_retorno_w		varchar(50);


BEGIN
select	max(a.ds_grupo),
	max(a.nr_sequencia)
into STRICT	ds_grupo_w,
	nr_seq_grupo_imp_w
from	lab_grupo_impressao a,
	exame_lab_grupo_imp b
where	b.nr_seq_exame = nr_seq_exame_p
and	a.nr_sequencia = b.nr_seq_grupo_imp
and	coalesce(b.cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
and	coalesce(b.cd_convenio, coalesce(cd_convenio_p,0)) = coalesce(cd_convenio_p,0);

if (coalesce(ds_grupo_w::text, '') = '') then
	select	max(a.ds_grupo),
		max(a.nr_sequencia)
	into STRICT	ds_grupo_w,
		nr_seq_grupo_imp_w
	from	lab_grupo_impressao a,
		exame_laboratorio b
	where	b.nr_seq_exame = nr_seq_exame_p
	and	a.nr_sequencia = b.nr_seq_grupo_imp;
end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w := nr_seq_grupo_imp_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w := ds_grupo_w;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
