SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_grupo_local_pa ( nr_seq_local_pa_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_grupo_w		bigint;
ds_grupo_w		varchar(90);
ds_nome_curto_w		varchar(30);
ie_situacao_w		varchar(2);

/*
ie_opcao:
C = codigo
D = descricao
A = nome curto
*/
BEGIN
begin
select	a.nr_sequencia,
	a.ds_grupo,
	a.ds_nome_curto,
	coalesce(a.ie_situacao,'A')
into STRICT	nr_seq_grupo_w,
	ds_grupo_w,
	ds_nome_curto_w,
	ie_situacao_w
from	pa_grupo_local a,
	pa_local b
where	a.nr_sequencia	= b.nr_seq_grupo_pa
and	b.nr_sequencia	= nr_seq_local_pa_p;

exception
	when others then
	null;
end;

if (ie_opcao_p = 'C') then
	return	nr_seq_grupo_w;
elsif (ie_opcao_p = 'D') then
	return	ds_grupo_w;
elsif (ie_opcao_p = 'A') then
	return	ds_nome_curto_w;
elsif (ie_opcao_p = 'S') then
	return ie_situacao_w;
else
	return	' ';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
