CREATE OR REPLACE FUNCTION obter_grupo_medic_microorg ( nr_seq_microorg_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_seq_material_w		bigint;
cd_microorganismo_w		bigint;
cd_medicamento_w		bigint;
ds_retorno_w			varchar(250);


BEGIN

select  coalesce(max(d.nr_seq_material),0),
	coalesce(max(a.cd_microorganismo),0),
	coalesce(max(a.cd_medicamento),0)
into STRICT	nr_seq_material_w,
	cd_microorganismo_w,
	cd_medicamento_w
from    exame_lab_result_antib a,
        exame_lab_result_item d
where   d.nr_seq_resultado   = a.nr_seq_resultado
and     d.nr_sequencia       = a.nr_seq_result_item
and	a.nr_sequencia	     = nr_seq_microorg_p;


SELECT 	coalesce(MAX(ds_grupo),'')
INTO STRICT	ds_retorno_w
FROM	cih_grupo_microorg	b,
	cih_microorg_medic	a
WHERE	a.nr_seq_grupo_micro 	= b.nr_sequencia
AND	a.cd_microorganismo 	= coalesce( cd_microorganismo_w,a.cd_microorganismo)
AND	a.nr_seq_material	= coalesce( nr_seq_material_w, a.nr_seq_material)
AND	a.cd_medicamento	= cd_medicamento_w;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
