CREATE OR REPLACE FUNCTION obter_grupo_motivo_glosa (cd_motivo_glosa_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_seq_grupo_w	bigint;
ds_grupo_w	varchar(255);


BEGIN


select 	max(a.nr_sequencia) cd,
	max(a.ds_grupo) ds
into STRICT	nr_seq_grupo_w,
	ds_grupo_w
from 	grupo_glosa a,
	motivo_glosa b
where 	a.nr_sequencia 	= b.nr_seq_grupo
and   	b.cd_motivo_glosa = cd_motivo_glosa_p;

if (ie_opcao_p = 'D') then
	ds_retorno_w := ds_grupo_w;
else
	ds_retorno_w := nr_seq_grupo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
