CREATE OR REPLACE FUNCTION obter_grupo_organograma (nm_usuario_p text, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE



nr_seq_grupo_w		bigint;


BEGIN

select	max(b.nr_sequencia)
into STRICT	nr_seq_grupo_w
from	gerencia_wheb_grupo b,
	gerencia_wheb_grupo_usu a
where	a.nr_seq_grupo		= b.nr_sequencia
and	b.ie_situacao		= 'A'
and	dt_referencia_p between coalesce(a.dt_inicio,dt_referencia_p) and coalesce(a.dt_fim,dt_referencia_p)
and	a.nm_usuario_grupo	= nm_usuario_p;

return nr_seq_grupo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
