SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_grupo_pessoa ( cd_pessoa_fisica_p text, dt_referencia_p timestamp default null) RETURNS bigint AS $body$
DECLARE


nr_seq_grupo_w	bigint;
nm_usuario_w	varchar(250);
dt_referencia_w	timestamp;


BEGIN

dt_referencia_w := coalesce(dt_referencia_p, clock_timestamp());

select	max(nm_usuario)
into STRICT	nm_usuario_w
from	usuario
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	ie_situacao = 'A';

select	max(a.nr_seq_grupo)
into STRICT	nr_seq_grupo_w
from	usuario_grupo_des a,
	grupo_desenvolvimento b
where	a.nm_usuario_grupo = nm_usuario_w
and 	a.nr_seq_grupo = b.nr_sequencia
and 	coalesce(b.ie_situacao,'A') = 'A'
and	trunc(dt_referencia_w) between trunc(coalesce(a.dt_inicio_vigencia, dt_referencia_w)) and trunc(coalesce(a.dt_fim_vigencia, dt_referencia_w))
and	coalesce(a.ie_emprestimo, 'N') = 'N';

return nr_seq_grupo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
