CREATE OR REPLACE FUNCTION obter_grupo_pj_rel_cre (cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(20);
/*OS1455684 - Function utilizada na aba Relat da função Manuteção de Títulos a Receber*/

BEGIN

if (coalesce(cd_cgc_p,'X') <> 'X') then

	select	substr(max(a.cd_cnpj),1,20)
	into STRICT	ds_retorno_w
	from	pessoa_juridica_grupo a
	where	a.cd_cnpj_unidade = cd_cgc_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
