CREATE OR REPLACE FUNCTION obter_grupo_procedimento (cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_cod_desc_p text) RETURNS varchar AS $body$
DECLARE


CD_GRUPO_PROC_w		bigint;
DS_GRUPO_PROC_w		varchar(255);


BEGIN
if (coalesce(cd_procedimento_p,0) > 0) then
   SELECT	cd_grupo_proc,
      ds_grupo_proc
   INTO STRICT	cd_grupo_proc_w,
      ds_grupo_proc_w
   FROM	estrutura_procedimento_v
   WHERE	cd_procedimento		= cd_procedimento_p
   AND	ie_origem_proced	= ie_origem_proced_p;
end if;

if (ie_cod_desc_p = 'C') then
   RETURN	cd_GRUPO_PROC_w;
else
   RETURN	DS_GRUPO_PROC_w;
end if;

END;
$body$
LANGUAGE PLPGSQL
