CREATE OR REPLACE FUNCTION obter_grupo_proc_aih ( cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
 IE_OPCAO_P
  C - Código
  D - Descrição
*/
cd_grupo_proc_aih_w	integer;
ds_grupo_proc_aih_w	varchar(240);
ds_retorno_w		varchar(240)	:= '';


BEGIN

select	coalesce(cd_grupo_proc_aih,0)
into STRICT	cd_grupo_proc_aih_w
from	procedimento
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

if (cd_grupo_proc_aih_w	> 0) then
	ds_retorno_w	:= cd_grupo_proc_aih_w;
	if (ie_opcao_p = 'D') then
		select	ds_grupo_proc_aih
		into STRICT	ds_grupo_proc_aih_w
		from	sus_grupo_proc_aih
		where	cd_grupo_proc_aih	= cd_grupo_proc_aih_w;

		ds_retorno_w	:= ds_grupo_proc_aih_w;
	end if;
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
