CREATE OR REPLACE FUNCTION obter_grupo_prod_cobranca (nr_seq_cobranca_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_titulo_w		bigint;
nr_seq_cheque_w		bigint;
nr_seq_grupo_prod_w	bigint;
ds_retorno_w		varchar(255);


BEGIN

select	nr_titulo,
	nr_seq_cheque
into STRICT	nr_titulo_w,
	nr_seq_cheque_w
from	cobranca
where	nr_sequencia	= nr_seq_cobranca_p;

if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then
	select	max(nr_seq_grupo_prod)
	into STRICT	nr_seq_grupo_prod_w
	from	titulo_receber
	where	nr_titulo	= nr_titulo_w;
elsif (nr_seq_cheque_w IS NOT NULL AND nr_seq_cheque_w::text <> '') then
	select	max(nr_seq_grupo_prod)
	into STRICT	nr_seq_grupo_prod_w
	from	cheque_cr
	where	nr_seq_cheque	= nr_seq_cheque_w;
end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= nr_seq_grupo_prod_w;
elsif (ie_opcao_p = 'D') then
	select	substr(ds_grupo,1,255)
	into STRICT	ds_retorno_w
	from	grupo_prod_financ
	where	nr_sequencia	= nr_seq_grupo_prod_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
