CREATE OR REPLACE FUNCTION obter_grupo_receita (CD_ESTABELECIMENTO_P bigint, CD_MATERIAL_P bigint, CD_PROCEDIMENTO_P bigint, IE_ORIGEM_PROCED_P bigint, IE_TIPO_ATENDIMENTO_P bigint, CD_SETOR_ATENDIMENTO_P bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_grupo_w			bigint;
nr_seq_grupo_val_w		bigint;
cd_grupo_proc_w			bigint		:= 0;
cd_espec_proc_w			bigint		:= 0;
cd_area_proc_w			bigint		:= 0;
cd_grupo_mat_w			smallint		:= 0;
cd_subgrupo_mat_w			smallint		:= 0;
cd_classe_mat_w			integer		:= 0;
nr_seq_grupo_sus_w		bigint;
nr_seq_subgrupo_sus_w		bigint;
nr_seq_forma_org_w		bigint;


C01 CURSOR FOR
	SELECT 	nr_seq_grupo
	from	grupo_rec_criterio
	where	cd_estabelecimento				= cd_estabelecimento_p
	and	coalesce(cd_procedimento,cd_procedimento_p) 		= cd_procedimento_p
	and	coalesce(ie_origem_proced,ie_origem_proced_p)	= ie_origem_proced_p
	and	coalesce(cd_area_proced,cd_area_proc_w)		= cd_area_proc_w
	and	coalesce(cd_especial_proced,cd_espec_proc_w)		= cd_espec_proc_w
	and	coalesce(cd_grupo_proced,cd_grupo_proc_w)		= cd_grupo_proc_w
	and	coalesce(nr_seq_forma_org,nr_seq_forma_org_w)	= nr_seq_forma_org_w
	and	coalesce(nr_seq_grupo_sus,nr_seq_grupo_sus_w)	= nr_seq_grupo_sus_w
	and	coalesce(nr_seq_subgrupo_sus,nr_seq_subgrupo_sus_w)	= nr_seq_subgrupo_sus_w
      	and 	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_p)	= ie_tipo_atendimento_p
	and	coalesce(cd_setor_atendimento, cd_setor_atendimento_p)	= cd_setor_atendimento_p
	and (coalesce(cd_material::text, '') = '') and (coalesce(cd_grupo_material::text, '') = '')
	and (coalesce(cd_subgrupo_material::text, '') = '') and (coalesce(cd_classe_material::text, '') = '')
	and (coalesce(nr_seq_estrutura::text, '') = '')
	order by
			coalesce(cd_procedimento,0),
			coalesce(cd_grupo_proced,0),
			coalesce(cd_especial_proced,0),
			coalesce(cd_area_proced,0),
			coalesce(cd_setor_atendimento,0),
			coalesce(ie_tipo_atendimento,0),
			coalesce(nr_seq_grupo_sus,0),
			coalesce(nr_seq_subgrupo_sus,0),
			coalesce(nr_seq_forma_org,0);

C02 CURSOR FOR
	SELECT 	nr_seq_grupo
	from	grupo_rec_criterio
	where	cd_estabelecimento				= cd_estabelecimento_p
	and	coalesce(cd_material,cd_material_p) 			= cd_material_p
	and	coalesce(cd_grupo_material,cd_grupo_mat_w) 		= cd_grupo_mat_w
	and	coalesce(cd_subgrupo_material,cd_subgrupo_mat_w) 	= cd_subgrupo_mat_w
	and	coalesce(cd_classe_material,cd_classe_mat_w) 	= cd_classe_mat_w
      and 	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_p)	= ie_tipo_atendimento_p
	and	coalesce(cd_setor_atendimento, cd_setor_atendimento_p)	= cd_setor_atendimento_p
	and (coalesce(cd_procedimento::text, '') = '') and (coalesce(cd_grupo_proced::text, '') = '')
	and (coalesce(cd_especial_proced::text, '') = '') and (coalesce(cd_area_proced::text, '') = '')
	and	((coalesce(nr_seq_estrutura::text, '') = '') or (consistir_se_mat_estrutura(nr_seq_estrutura,cd_material_p) = 'S'))
	order by
			coalesce(cd_material,0),
			coalesce(nr_seq_estrutura,0),
			coalesce(cd_grupo_material,0),
			coalesce(cd_subgrupo_material,0),
			coalesce(cd_classe_material,0),
			coalesce(cd_setor_atendimento,0),
			coalesce(ie_tipo_atendimento,0);


BEGIN

nr_seq_grupo_val_w	:= null;
/* Obter Estrutura do procedimento */

if (cd_procedimento_p > 0) then
	BEGIN
	select		max(nr_seq_grupo_rec)
	into STRICT		nr_seq_grupo_val_w
	from		Procedimento
	where		cd_procedimento		= cd_procedimento_p
	and		ie_origem_proced	= ie_origem_proced_p;
	if (coalesce(nr_seq_grupo_val_w::text, '') = '') then
                BEGIN
		begin
		select 	cd_grupo_proc,
			cd_especialidade,
			cd_area_procedimento
		into STRICT	cd_grupo_proc_w,
			cd_espec_proc_w,
			cd_area_proc_w
		from	Estrutura_Procedimento_V
		where	cd_procedimento 	= cd_procedimento_p
		and	ie_origem_proced	= ie_origem_proced_p;
		exception
     			when others then
				cd_grupo_proc_w			:= 0;
		end;

		begin
		select	nr_seq_grupo,
			nr_seq_subgrupo,
			nr_seq_forma_org
		into STRICT	nr_seq_grupo_sus_w,
		        nr_seq_subgrupo_sus_w,
		        nr_seq_forma_org_w
		from	sus_estrutura_procedimento_v
		where	cd_procedimento 	= cd_procedimento_p
		and	ie_origem_proced	= ie_origem_proced_p;
		exception
			when others then
			nr_seq_grupo_sus_w	:= 0;
		        nr_seq_subgrupo_sus_w	:= 0;
		        nr_seq_forma_org_w	:= 0;
		end;

		begin
		OPEN C01;
		LOOP
		FETCH C01 into
			nr_seq_grupo_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			nr_seq_grupo_val_w	:= nr_seq_grupo_w;
			end;
		END LOOP;
		CLOSE C01;
		end;
		END;
	end if;
	END;
end if;

/* Obter Estrutura do material */

if (cd_material_p > 0) then
	BEGIN
	select	max(nr_seq_grupo_rec)
	into STRICT	nr_seq_grupo_val_w
	from	Material
	where	cd_material	= cd_material_p;
	if (coalesce(nr_seq_grupo_val_w::text, '') = '') then
                BEGIN
		begin
		select 	cd_grupo_material,
			cd_subgrupo_material,
			cd_classe_material
		into STRICT	cd_grupo_mat_w,
			cd_subgrupo_mat_w,
			cd_classe_mat_w
		from 	estrutura_material_v
		where 	cd_material		= cd_material_p;
		exception
     			when others then
			cd_grupo_mat_w			:= 0;
		end;

		begin
		OPEN C02;
		LOOP
		FETCH C02 into
			nr_seq_grupo_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			nr_seq_grupo_val_w	:= nr_seq_grupo_w;
			end;
		END LOOP;
		CLOSE C02;
		end;
		END;
	end if;
	END;
end if;

RETURN nr_seq_grupo_val_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_grupo_receita (CD_ESTABELECIMENTO_P bigint, CD_MATERIAL_P bigint, CD_PROCEDIMENTO_P bigint, IE_ORIGEM_PROCED_P bigint, IE_TIPO_ATENDIMENTO_P bigint, CD_SETOR_ATENDIMENTO_P bigint) FROM PUBLIC;

