CREATE OR REPLACE FUNCTION obter_grupo_subgrupo_fam_mat (CD_CODIGO_P bigint, IE_TIPO_P text) RETURNS varchar AS $body$
DECLARE


DS_RETORNO_W		varchar(255);


BEGIN

IF (IE_TIPO_P = 'GM') THEN

SELECT SUBSTR((SELECT MAX(A.DS_GRUPO_MATERIAL) FROM GRUPO_MATERIAL A
WHERE A.CD_GRUPO_MATERIAL = CD_CODIGO_P),1,255) 
INTO STRICT 	DS_RETORNO_W
;

ELSIF (IE_TIPO_P = 'SM') THEN

SELECT SUBSTR((SELECT MAX(A.DS_SUBGRUPO_MATERIAL)
FROM SUBGRUPO_MATERIAL A 
WHERE A.CD_SUBGRUPO_MATERIAL = CD_CODIGO_P),1,255)
INTO STRICT DS_RETORNO_W
;

ELSIF (IE_TIPO_P = 'FA') THEN

SELECT SUBSTR((SELECT MAX(DS_FAMILIA)
FROM MATERIAL_FAMILIA 
WHERE NR_SEQUENCIA = CD_CODIGO_P),1,255)
INTO STRICT DS_RETORNO_W
;

END IF;

RETURN DS_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
