SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_guias_conta (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


cd_autorizacao_w		varchar(20) := '';
ds_guias_w			varchar(2000);/*Elemar - 230605*/
c01 CURSOR FOR
	SELECT cd_autorizacao
	from conta_paciente_guia
	where nr_interno_conta = nr_interno_conta_p
	order by cd_autorizacao;


BEGIN
ds_guias_w		:= '';
OPEN C01;
LOOP
FETCH C01 into cd_autorizacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (coalesce(ds_guias_w::text, '') = '') then
		ds_guias_w	:= cd_autorizacao_w;
	else
		ds_guias_w	:= ds_guias_w || ',' || cd_autorizacao_w;
	end if;
END LOOP;
CLOSE C01;
RETURN ds_guias_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
