CREATE OR REPLACE FUNCTION obter_guias_outro_retorno (nr_seq_retorno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_contas_w		varchar(4000)	:= null;
qt_registro_w		bigint;
nr_interno_conta_w	bigint;
cd_autorizacao_w	varchar(20);

c01 CURSOR FOR
SELECT	nr_interno_conta,
	cd_autorizacao
from	convenio_retorno_item
where	nr_seq_retorno	= nr_seq_retorno_p;


BEGIN

if (nr_seq_retorno_p IS NOT NULL AND nr_seq_retorno_p::text <> '') then

	open c01;
	loop
	fetch c01 into
		nr_interno_conta_w,
		cd_autorizacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		select	count(*)
		into STRICT	qt_registro_w
		from	convenio_retorno b,
			convenio_retorno_item a
		where	a.nr_seq_retorno	= b.nr_sequencia
		and	nr_interno_conta	= nr_interno_conta_w
		and	a.cd_autorizacao	= cd_autorizacao_w
		and	b.ie_status_retorno	<> 'F'
		and	nr_seq_retorno		<> nr_seq_retorno_p;

		if (qt_registro_w > 0) and (length(ds_contas_w || ',' || nr_interno_conta_w || '/' || cd_autorizacao_w) < 4000) then
			if (coalesce(ds_contas_w::text, '') = '') then
				ds_contas_w	:= nr_interno_conta_w || '/' || cd_autorizacao_w;
			else
				ds_contas_w	:= ds_contas_w || ',' || nr_interno_conta_w || '/' || cd_autorizacao_w;
			end if;
		end if;

	end loop;
	close c01;
end if;

return ds_contas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
