CREATE OR REPLACE FUNCTION obter_guias_saldo_pendente (nr_seq_retorno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_guias_w		varchar(2000);
vl_glosa_w		double precision;
vl_soma_itens_ret_w	double precision;
nr_seq_ret_item_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		coalesce(vl_glosado,0) + coalesce(vl_amenor,0)
	from	convenio_retorno_item
	where	nr_seq_retorno = nr_seq_retorno_p;


BEGIN
if (nr_seq_retorno_p IS NOT NULL AND nr_seq_retorno_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_seq_ret_item_w,
		vl_soma_itens_ret_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	coalesce(sum(vl_glosa),0)
		into STRICT	vl_glosa_w
		from	convenio_retorno_glosa
		where	nr_seq_ret_item = nr_seq_ret_item_w;

		if (vl_glosa_w > vl_soma_itens_ret_w) then
			ds_guias_w := ds_guias_w || nr_seq_ret_item_w || ',';
		end if;

		end;
	end loop;
	close C01;

end if;

return	ds_guias_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
