CREATE OR REPLACE FUNCTION obter_guia_conta ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
cd_autorizacao_w	varchar(20);

 

BEGIN 
 
select	cd_autorizacao 
into STRICT	cd_autorizacao_w 
from	conta_paciente 
where	nr_interno_conta	= nr_interno_conta_p;
 
 
if (coalesce(cd_autorizacao_w::text, '') = '') then 
	 
	select	max(cd_autorizacao) 
	into STRICT	cd_autorizacao_w 
	from	conta_paciente_guia 
	where	nr_interno_conta		=  nr_interno_conta_p 
	and	somente_numero(cd_autorizacao)	<> 0;
	 
end if;
 
if (coalesce(cd_autorizacao_w::text, '') = '') then 
	select	obter_guia_convenio(nr_atendimento) 
	into STRICT	cd_autorizacao_w 
	from	conta_paciente 
	where	nr_interno_conta	= nr_interno_conta_p;
 
end if;
 
 
return cd_autorizacao_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
