CREATE OR REPLACE FUNCTION obter_guia_convenio_princ2 (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


NR_DOC_CONV_PRINCIPAL_w		varchar(20);


BEGIN

/*
Craido para a view ocupacao_unidade_v
*/
SELECT	max(NR_DOC_CONV_PRINCIPAL)
INTO STRICT	NR_DOC_CONV_PRINCIPAL_w
FROM	atend_categoria_convenio
WHERE	nr_seq_interno = (SELECT	coalesce(max(nr_seq_interno),0)
			from 	atend_categoria_convenio a
			where	a.nr_atendimento 		= nr_atendimento_p
			and 	dt_inicio_vigencia=
			(select max(dt_inicio_vigencia)
			from atend_categoria_convenio b
			where nr_atendimento 		= nr_atendimento_p));

RETURN	NR_DOC_CONV_PRINCIPAL_w;

END	;
