SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_guia_senha_prescr ( nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
G - Guia;
S - Senha;
*/
ds_retorno_w		varchar(20);
nr_guia_w		varchar(20);
cd_senha_w		varchar(20);


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) then
	select	a.nr_doc_conv,
		a.cd_senha
	into STRICT	nr_guia_w,
		cd_senha_w
	from	prescr_medica a
	where	a.nr_prescricao = nr_prescricao_p;

	if (ie_opcao_p	= 'G')	then
		ds_retorno_w	:= nr_guia_w;
	elsif (ie_opcao_p	= 'S')	then
		ds_retorno_w	:= cd_senha_w;
	end if;
end if;

Return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
