CREATE OR REPLACE FUNCTION obter_guiche_maquina_curto (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_guiche_w			varchar(255);
ds_maquina_w			varchar(255);


BEGIN

select	max(DS_MAQUINA_CHAMADA)
into STRICT	ds_maquina_w
from	paciente_senha_fila
where	nr_sequencia = nr_sequencia_p;


select	max(coalesce(a.ds_local_curto,''))
into STRICT	ds_guiche_w
from	maquina_local_senha a,
	computador b
where	a.nr_seq_computador	= b.nr_sequencia
and	nm_computador_pesquisa	= upper(ds_maquina_w)
and	coalesce(a.ie_situacao, 'A') = 'A';

return ds_guiche_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
