SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_habilita_botao_auxiliar (cd_estab_p bigint, ie_sistema_externo_p text) RETURNS varchar AS $body$
DECLARE


cd_cgc_cliente_w	varchar(14);
ie_retorno_w		varchar(1) := 'N';


BEGIN
if (cd_estab_p IS NOT NULL AND cd_estab_p::text <> '') and (ie_sistema_externo_p IS NOT NULL AND ie_sistema_externo_p::text <> '') then
	select	max(obter_cgc_estabelecimento(cd_estab_p))
	into STRICT	cd_cgc_cliente_w
	;

	select	coalesce(max('S'), 'N')
	into STRICT 	ie_retorno_w
	from	liberacao_acesso_ext_pep
	where 	ie_sistema_externo 	= ie_sistema_externo_p
  and (cd_cgc_cliente = cd_cgc_cliente_w or (substr(cd_cgc_cliente, 1, 8) = substr(cd_cgc_cliente_w, 1, 8) and ie_sistema_externo_p = 'HSHARE'));
	
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
