SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_help_funcao ( cd_funcao_p bigint, ie_responsavel_p text) RETURNS varchar AS $body$
DECLARE


/* Function que retorna se  os ítens do responsável estão todos preenchidos*/

qt_registros_w			bigint;
ds_retorno_w			varchar(1)	:= 'N';


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	tasy_tipo_help a
where	a.ie_responsavel	= ie_responsavel_p
and	a.nr_sequencia not in (	SELECT	distinct a.nr_sequencia
				from	tasy_tipo_help a,
					tasy_help b,
					tasy_objeto_help c
				where	a.nr_sequencia		= b.nr_seq_tipo
				and	c.nr_sequencia		= b.nr_seq_objeto
				and	a.ie_responsavel	= ie_responsavel_p
				and	c.cd_funcao		= cd_funcao_p);

if (qt_registros_w	= 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
