SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_hint_medicamento (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


qt_limite_pessoa_w		double precision;
ds_orientacao_usuario_w		varchar(4000);
ds_reacao_w			varchar(4000);
ds_hint_w			varchar(10000);


BEGIN

select	max(qt_limite_pessoa),
	max(ds_orientacao_usuario)
into STRICT	qt_limite_pessoa_w,
	ds_orientacao_usuario_w
from	material
where	cd_material	= cd_material_p;

select	max(ds_reacao)
into STRICT	ds_reacao_w
from	material_reacao
where	cd_material	= cd_material_p;

if (qt_limite_pessoa_w IS NOT NULL AND qt_limite_pessoa_w::text <> '') then
	ds_hint_w	:= wheb_mensagem_pck.get_texto(309141) || ': ' || qt_limite_pessoa_w; -- Dose limite uso por pessoa
end if;

if (ds_orientacao_usuario_w IS NOT NULL AND ds_orientacao_usuario_w::text <> '') then
	if (ds_hint_w IS NOT NULL AND ds_hint_w::text <> '') then
		ds_hint_w	:= ds_hint_w || chr(13) || ' ' || wheb_mensagem_pck.get_texto(309142) || ': ' || ds_orientacao_usuario_w; -- Orientação ao usuário
	else
		ds_hint_w	:= ' ' || wheb_mensagem_pck.get_texto(309142) || ': ' || ds_orientacao_usuario_w; -- Orientação ao usuário
	end if;
end if;

if (ds_reacao_w IS NOT NULL AND ds_reacao_w::text <> '') then
	if (ds_hint_w IS NOT NULL AND ds_hint_w::text <> '') then
		ds_hint_w	:= ds_hint_w || chr(13) || ' ' || wheb_mensagem_pck.get_texto(309143) || ': ' || ds_reacao_w; -- Reações adversas
	else
		ds_hint_w	:= ' ' || wheb_mensagem_pck.get_texto(309143) || ': ' || ds_reacao_w; -- Reações adversas
	end if;
end if;

return	substr(ds_hint_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
