CREATE OR REPLACE FUNCTION obter_hiperhidratacao ( cd_pessoa_fisica_p text ) RETURNS bigint AS $body$
DECLARE

				
qt_hiperhidratacao_w		double precision;				


BEGIN

select	coalesce(max(qt_hiperhidratacao),0)
into STRICT	qt_hiperhidratacao_w
from	atend_bioimpedancia
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	ie_situacao = 'A'
and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and     dt_atualizacao_nrec = (SELECT max(dt_atualizacao_nrec)
				from	atend_bioimpedancia  
				where	cd_pessoa_fisica = cd_pessoa_fisica_p
				and	ie_situacao = 'A'
				and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''));

return	qt_hiperhidratacao_w;

end;
