CREATE OR REPLACE FUNCTION obter_hist_bomba_infusao (NR_SEQUENCIA_P bigint) RETURNS varchar AS $body$
DECLARE

  Result varchar(4000);
  traco varchar(200);
  nr_seq_equip_w bomba_infusao.nr_seq_equipamento%type;

  c01 CURSOR FOR
  SELECT a.*
  from BOMBA_INFUSAO_HIST a
  where a.nr_seq_bomba = nr_seq_equip_w;

BEGIN
  Result := '';

  SELECT nr_seq_equipamento INTO STRICT nr_seq_equip_w FROM bomba_infusao WHERE nr_sequencia = nr_sequencia_p;

  for r_c01 in c01 loop
    begin
     traco  := ' ................................................................................................. ';
     Result := Result || r_c01.DT_ATUALIZACAO || ': ' || r_c01.DS_HISTORICO || chr(10) || traco || traco || chr(10);
    end;
  end loop;
  return(Result);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
