SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_hist_conta_pac_audit (nr_interno_conta_p bigint, cd_autorizacao_p text, nr_seq_ret_hist_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_conta_retorno_w	bigint;
nr_lote_rec_w		bigint;
nr_lote_audit_w		bigint;
ie_acao_w		bigint;


BEGIN

select	max(a.nr_sequencia)
into STRICT	nr_seq_conta_retorno_w
from	conta_paciente_retorno a
where	nr_interno_conta	= nr_interno_conta_p
and	cd_autorizacao		= cd_autorizacao_p;

select	max(a.nr_seq_lote_recurso),
	max(a.nr_seq_lote_audit),
	max(b.ie_acao)
into STRICT	nr_lote_rec_w,
	nr_lote_audit_w,
	ie_acao_w
from	conta_paciente_ret_hist a,
	hist_audit_conta_paciente b
where	a.nr_seq_hist_audit	= b.nr_sequencia
and	a.nr_seq_conpaci_ret	= nr_seq_conta_retorno_w
and	a.nr_sequencia			= nr_seq_ret_hist_p;


if (ie_acao_w	= 4) then	--Ação Glosa Indevida
	if (nr_lote_rec_w IS NOT NULL AND nr_lote_rec_w::text <> '') then
		ds_retorno_w	:= wheb_mensagem_pck.get_texto(803001);
	else
		ds_retorno_w	:= wheb_mensagem_pck.get_texto(803004);
	end if;

elsif (ie_acao_w	= 3) then	--Ação Glosa Devida
	if (coalesce(nr_lote_rec_w::text, '') = '') and (coalesce(nr_lote_audit_w::text, '') = '') then

		ds_retorno_w	:= obter_desc_expressao(304489);--'Glosa devida';
	else
		ds_retorno_w	:= wheb_mensagem_pck.get_texto(803020);

	end if;

elsif (ie_acao_w = 0) then  --Ação Não Auditado
	ds_retorno_w	:= obter_desc_expressao(284037);--'Auditoria';
elsif (ie_acao_w = 1) then  --Recebimento
	ds_retorno_w	:= 'Acatadas';

else
	select	substr(obter_valor_dominio(1154, ie_acao_w),1,80)
	into STRICT	ds_retorno_w
	;
end if;

return	coalesce(ds_retorno_w,'Acatadas');
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
