CREATE OR REPLACE FUNCTION obter_hist_curto_mat (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_historico_curto_w	varchar(4000);
nr_sequencia_w		bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from   	material_historico
where  	cd_material = cd_material_p;


select	max(substr(ds_historico_curto,1,4000))
into STRICT	ds_historico_curto_w
from   	material_historico
where  	cd_material = cd_material_p
and	nr_sequencia = nr_sequencia_w;


return	ds_historico_curto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
