CREATE OR REPLACE FUNCTION obter_hist_ordem_serv_tec (nr_seq_ordem_serv_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);


BEGIN

if (nr_seq_ordem_serv_p IS NOT NULL AND nr_seq_ordem_serv_p::text <> '') then

	select	x.ds_relat_tecnico
	into STRICT	ds_retorno_w
	from	man_ordem_serv_tecnico x
	where	x.nr_sequencia = (SELECT	max(a.nr_sequencia)
			             from	man_ordem_serv_tecnico a
			             where	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
			             and	a.nr_seq_ordem_serv = nr_seq_ordem_serv_p);

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
