SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_hist_transfusao ( nr_sequencia_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);


BEGIN

if (ie_opcao_p = 1) then
	select	wheb_mensagem_pck.get_texto(796622) || ': ' || dt_transfusao||CHR(13)||CHR(10)||
		wheb_mensagem_pck.get_texto(796623) || ': ' || nr_sangue||chr(13)||CHR(10)||
		wheb_mensagem_pck.get_texto(796651) || ':  ' || ie_irradiado||chr(13)||CHR(10)||
		wheb_mensagem_pck.get_texto(796652) || ': 	 ' || ie_lavado||chr(13)||CHR(10)||
		wheb_mensagem_pck.get_texto(796657) || ':	 ' || ie_filtrado||chr(13)||CHR(10)||
		wheb_mensagem_pck.get_texto(796658) || ': ' || ie_aliquotado||chr(13)||CHR(10)||
		wheb_mensagem_pck.get_texto(796659) || ':	 ' || ie_aferese
	into STRICT	ds_retorno_w
	from	paciente_transfusao
	where	nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 2) then
	select	wheb_mensagem_pck.get_texto(796622) || ': ' || b.dt_transfusao||chr(13)||CHR(10)||
		wheb_mensagem_pck.get_texto(796623) || ': ' || a.nr_sangue||chr(13)||CHR(10)||
		wheb_mensagem_pck.get_texto(796651) || ':  ' || a.ie_irradiado||chr(13)||CHR(10)||
		wheb_mensagem_pck.get_texto(796652) || ': 	 ' || a.ie_lavado||chr(13)||CHR(10)||
		wheb_mensagem_pck.get_texto(796657) || ':	 ' || a.ie_filtrado||chr(13)||CHR(10)||
		wheb_mensagem_pck.get_texto(796658) || ': ' || a.ie_aliquotado||chr(13)||CHR(10)||
		wheb_mensagem_pck.get_texto(796659) || ':	 ' || a.ie_aferese

	into STRICT	ds_retorno_w
	from	san_producao a,
			san_transfusao b
	WHERE 	a.nr_seq_transfusao = b.nr_sequencia
	and		a.nr_sequencia = nr_sequencia_p;
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
