CREATE OR REPLACE FUNCTION obter_hist_trans_financ (nr_seq_trans_financ_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_historico_w		varchar(255);

c01 CURSOR FOR
SELECT	a.ds_historico
from	historico_padrao a,
	trans_financ_contab b
where	b.cd_historico		= a.cd_historico
and	b.nr_seq_trans_financ	= nr_seq_trans_financ_p
and	b.ie_situacao 		= 'A';


BEGIN
ds_retorno_w	:= '';

open c01;
loop
fetch c01 into
	ds_historico_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_retorno_w	:= ds_historico_w || chr(13);
end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
