SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_honor_fora_pacote (nr_seq_proc_pacote_p bigint) RETURNS bigint AS $body$
DECLARE


/* Obter valor de honor√°rio do procedimento de origem do pacote.
   Criado para HVC, pacotes do IPSEMG - Elemar - 26/08/2003 */
vl_medico_w          double precision;
vl_partic_w		double precision;


BEGIN

if (nr_seq_proc_pacote_p IS NOT NULL AND nr_seq_proc_pacote_p::text <> '') then
	select     coalesce(sum(vl_medico),0)
	into STRICT       vl_medico_w
	from       procedimento_paciente b,
	   atendimento_pacote a
	where b.nr_sequencia = a.nr_seq_proc_origem
	  and a.nr_seq_procedimento = nr_seq_proc_pacote_p;

	select     coalesce(sum(vl_conta),0)
	into STRICT       vl_partic_w
	from   procedimento_participante c,
		procedimento_paciente b,
	   	atendimento_pacote a
	where b.nr_sequencia = a.nr_seq_proc_origem
	  and b.nr_sequencia = c.nr_sequencia
	  and a.nr_seq_procedimento = nr_seq_proc_pacote_p;
end if;

RETURN coalesce(vl_medico_w,0) + coalesce(vl_partic_w,0);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_honor_fora_pacote (nr_seq_proc_pacote_p bigint) FROM PUBLIC;

