CREATE OR REPLACE FUNCTION obter_horarios_adm_susp_sol ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
dt_horario_w	varchar(20);
c01 CURSOR FOR
SELECT CASE WHEN coalesce(obter_status_etapa_sol(nr_prescricao,nr_seq_solucao,nr_etapa_sol)::text, '') = '' THEN to_char(dt_horario,'hh24:mi') WHEN obter_status_etapa_sol(nr_prescricao,nr_seq_solucao,nr_etapa_sol)='T' THEN to_char(dt_horario,'hh24:mi')|| chr('174') WHEN obter_status_etapa_sol(nr_prescricao,nr_seq_solucao,nr_etapa_sol)='S' THEN to_char(dt_horario,'hh24:mi')|| chr('169') END
from (select	distinct dt_horario,
		nr_prescricao,
		nr_seq_solucao,
		nr_etapa_sol
	 from	prescr_mat_hor
	 where	nr_prescricao = nr_prescricao_p
	 and	nr_seq_solucao = nr_seq_solucao_p
	 order by dt_horario) alias9;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then
	open c01;
	loop
	fetch c01 into dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
				ds_retorno_w := ds_retorno_w||' '||dt_horario_w;
			else
				ds_retorno_w := dt_horario_w;
			end if;
		end;
	end loop;
	close c01;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
