CREATE OR REPLACE FUNCTION obter_horarios_adm_susp_so ( nr_prescricao_p bigint, nr_seq_material_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
dt_horario_w	varchar(20);
c01 CURSOR FOR
SELECT	CASE WHEN coalesce(dt_suspensao::text, '') = '' THEN  CASE WHEN coalesce(dt_fim_horario::text, '') = '' THEN to_char(dt_horario,'hh24:mi')  ELSE to_char(dt_horario,'hh24:mi')|| chr('174') END   ELSE to_char(dt_horario,'hh24:mi')|| chr('169') END
from	prescr_mat_hor
where	nr_prescricao	= nr_prescricao_p
and 	nr_seq_material = nr_seq_material_p
and	cd_material	= cd_material_p
and	ie_agrupador	= 12
and	coalesce(ie_horario_especial,'N') = 'N'
order by dt_horario;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	open c01;
	loop
	fetch c01 into dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
				ds_retorno_w := ds_retorno_w||' '||dt_horario_w;
			else
				ds_retorno_w := dt_horario_w;
			end if;
		end;
	end loop;
	close c01;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
