CREATE OR REPLACE FUNCTION obter_horarios_dieta_oral ( nr_prescricao_p bigint, nr_seq_dieta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		prescr_dieta.ds_horarios%type;
ds_hor_w		varchar(255);

c01 CURSOR FOR
SELECT	to_char(dt_horario,'hh24:mi')
from	prescr_dieta_hor
where	nr_prescricao	= nr_prescricao_p
and	nr_seq_dieta	= nr_seq_dieta_p
and	coalesce(dt_suspensao::text, '') = ''
order by dt_horario;


BEGIN

open C01;
loop
fetch C01 into
	ds_hor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_resultado_w := ds_resultado_w || ' ' || ds_hor_w;
end loop;
close C01;


return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
