CREATE OR REPLACE FUNCTION obter_horarios_diluente ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_diluicao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);


BEGIN

select	coalesce(max(a.ds_horarios),' ')
into STRICT	ds_retorno_w
from	prescr_material a,
	prescr_medica m
where	m.nr_prescricao 	= a.nr_prescricao
and	a.nr_prescricao 	= nr_prescricao_p
and	a.nr_sequencia 	= coalesce(nr_seq_diluicao_p, nr_sequencia_p);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
