CREATE OR REPLACE FUNCTION obter_horarios_exec_proced ( nr_prescricao_p bigint, cd_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
dt_procedimento_w	timestamp;

c01 CURSOR FOR
	SELECT	dt_procedimento
	from	procedimento_paciente
	where	nr_prescricao		= nr_prescricao_p
	and	cd_procedimento		= cd_procedimento_p
	order by nr_sequencia_prescricao;


BEGIN

open c01;
loop
fetch c01 into
	dt_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		ds_retorno_w := ds_retorno_w || ' ' || to_char(dt_procedimento_w, 'hh24:mi') || ' ';
	end;
end loop;
close C01;

return '#' || ds_retorno_w || '#K#P ';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
