CREATE OR REPLACE FUNCTION obter_horarios_medic_adm (nr_prescricao_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_horario_w		varchar(50);
ds_horario_ww		varchar(50);
ds_horario_adm_w	varchar(50);
ds_horarios_w		varchar(2000);

c01 CURSOR FOR
SELECT	ds_horario
from	prescr_mat_hor
where	nr_prescricao 	= nr_prescricao_p
and	nr_seq_material = nr_seq_material_p
order by
	ds_horario;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
	open c01;
	loop
	fetch c01 into ds_horario_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	max('('||ds_horario||')') ds_horario_adm
		into STRICT 	ds_horario_adm_w
		from	prescr_mat_hor
		where	(dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '')
		and 	nr_prescricao 	= nr_prescricao_p
		and 	nr_seq_material = nr_seq_material_p
		and 	ds_horario	= ds_horario_w
		order by
			ds_horario;

		if (ds_horario_adm_w IS NOT NULL AND ds_horario_adm_w::text <> '') then
			if (coalesce(ds_horarios_w::text, '') = '') then
				ds_horarios_w := ds_horario_adm_w;
			else
				ds_horarios_w := ds_horarios_w||' '||ds_horario_adm_w;
			end if;
		else
			if (coalesce(ds_horarios_w::text, '') = '') then
				ds_horarios_w := ds_horario_w;
			else
				ds_horarios_w := ds_horarios_w||' '||ds_horario_w;
			end if;
		end if;

		end;
	end loop;
	close c01;

return ds_horarios_w;

end if;

end;
$body$
