CREATE OR REPLACE FUNCTION obter_horarios_medic ( nr_prescricao_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_horario_w		varchar(15);
ds_resultado_w		varchar(2000);
dt_horario_w		timestamp;

C01 CURSOR FOR
SELECT	to_char(b.dt_horario,'hh24:mi'),
	b.dt_horario
from	prescr_mat_hor b
where	b.nr_prescricao		= nr_prescricao_p
and	b.nr_seq_material	= nr_seq_material_p
and	coalesce(b.dt_suspensao::text, '') = ''
and	Obter_se_horario_liberado(b.dt_lib_horario, b.dt_horario) = 'S'
and	coalesce(b.ie_horario_especial,'N')	= 'N'
group by b.dt_horario
order by b.dt_horario;


BEGIN

open c01;
loop
fetch c01 into
	ds_horario_w,
	dt_horario_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(ds_resultado_w::text, '') = '') then
		ds_resultado_w	:= ds_horario_w;
	else
		ds_resultado_w	:= ds_resultado_w ||' '|| ds_horario_w;
	end if;
	end;
end loop;
close c01;

return ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
