SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_horarios_prescrito ( nr_prescricao_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ds_horarios_w           varchar(255);
dt_horario_w            varchar(255);

c01 CURSOR FOR
SELECT  to_char(dt_horario,'hh24:mi')
from    prescr_mat_hor
where   nr_prescricao   = nr_prescricao_p
and     nr_seq_material = nr_seq_item_p
and     coalesce(dt_suspensao::text, '') = ''
order by dt_horario asc;

c02 CURSOR FOR
SELECT  to_char(dt_horario,'hh24:mi')
from    prescr_proc_hor
where   nr_prescricao   	= nr_prescricao_p
and     nr_seq_procedimento	= nr_seq_item_p
and     coalesce(dt_suspensao::text, '') = ''
and		coalesce(ie_horario_especial, 'N') <> 'S'
order by dt_horario asc;

c03 CURSOR FOR
SELECT  to_char(dt_horario,'hh24:mi')
from    prescr_rec_hor
where   nr_prescricao   	= nr_prescricao_p
and     nr_seq_recomendacao	= nr_seq_item_p
and     coalesce(dt_suspensao::text, '') = ''
order by dt_horario asc;


BEGIN

if (ie_tipo_item_p in ('S','M','MAT')) then

	open C01;
	loop
	fetch C01 into
		dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ds_horarios_w   := ds_horarios_w || ' ' || dt_horario_w;
	end loop;
	close C01;

elsif (ie_tipo_item_p in ('P','L')) then

	open C02;
	loop
	fetch C02 into
		dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		ds_horarios_w   := ds_horarios_w || ' ' || dt_horario_w;
	end loop;
	close C02;

elsif (ie_tipo_item_p = 'R') then

	open C03;
	loop
	fetch C03 into
		dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		ds_horarios_w   := ds_horarios_w || ' ' || dt_horario_w;
	end loop;
	close C03;

end if;

-- retirar espaco da frente da lista de horarios
return  LTRIM(ds_horarios_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_horarios_prescrito ( nr_prescricao_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text) FROM PUBLIC;

