CREATE OR REPLACE FUNCTION obter_horarios_suspensos_rep (nr_prescricao_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_horarios_w           varchar(255);
dt_horario_w            varchar(255);

c01 CURSOR FOR
SELECT  to_char(dt_horario,'hh24:mi')
from    prescr_mat_hor
where   nr_prescricao   = nr_prescricao_p
and     nr_seq_material = nr_seq_material_p
and     (dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';


BEGIN

open C01;
loop
fetch C01 into
        dt_horario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
        ds_horarios_w   := ds_horarios_w || ' ' || dt_horario_w;
end loop;
close C01;

return  ds_horarios_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
