CREATE OR REPLACE FUNCTION obter_horario_etapa_ntp ( nr_prescricao_p bigint, nr_seq_npt_p bigint, nr_etapa_atual_p bigint, ie_tipo_npt_p bigint) RETURNS timestamp AS $body$
DECLARE


qt_horarios_w	integer := 1;
dt_horario_w	timestamp;

C01 CURSOR FOR
	SELECT 	a.dt_horario
	from   	prescr_material b,
	  		prescr_mat_hor a
	where 	b.nr_seq_nut_pac  = nr_seq_npt_p
	and	  	b.nr_prescricao = a.nr_prescricao
	and	 	b.nr_sequencia  = a.nr_seq_material
	group by a.dt_horario
	order by 1;


BEGIN


	open C01;
	loop
	fetch C01 into
		dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (coalesce(nr_etapa_atual_p,1) = qt_horarios_w) then
			return dt_horario_w;
		end if;

		qt_horarios_w	:= qt_horarios_w + 1;

		end;
	end loop;
	close C01;

return null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
