CREATE OR REPLACE FUNCTION obter_horario_exec_prescr ( dt_prescricao_p timestamp, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS timestamp AS $body$
DECLARE


hr_horario_w	varchar(10);
dt_retorno_w	timestamp;


BEGIN

hr_horario_w := Obter_Param_Usuario(924, 45, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, hr_horario_w);

if (ie_opcao_p = '1') then
	if (campo_numerico(to_char(clock_timestamp(),'hh24')) > campo_numerico(coalesce(hr_horario_w,'6'))) then
		dt_retorno_w	:= trunc(dt_prescricao_p,'dd') + (campo_numerico(coalesce(hr_horario_w,'6'))/24) + 1;
	else
		dt_retorno_w	:= trunc(dt_prescricao_p,'dd') + (campo_numerico(coalesce(hr_horario_w,'6'))/24);
	end if;
elsif (ie_opcao_p = '2') then
	dt_retorno_w	:= trunc(dt_prescricao_p,'mi');
elsif (ie_opcao_p = '3') then
	if (dt_prescricao_p > clock_timestamp()) then
		dt_retorno_w	:= trunc(dt_prescricao_p + (1/24),'hh24');
	else
		dt_retorno_w	:= trunc(clock_timestamp() + (1/24),'hh24');
	end if;
elsif (ie_opcao_p = '4') then
	dt_retorno_w	:= trunc(dt_prescricao_p + (1/24),'hh24');
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
