SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_horario_fim_servico ( nr_seq_servico_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_horarios_w		nut_servico_horario.ds_horarios_fim%type;
nr_seq_horario_w	nut_servico_horario.nr_sequencia%type;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_horario_w
from	nut_servico_horario
where	nr_seq_servico = nr_seq_servico_p
and		cd_setor_atendimento = cd_setor_atendimento_p;

if (nr_seq_horario_w = 0) then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_horario_w
	from	nut_servico_horario
	where	nr_seq_servico = nr_seq_servico_p
	and		coalesce(cd_setor_atendimento::text, '') = '';

end if;

if (nr_seq_horario_w > 0) then
	select	max(ds_horarios_fim)
	into STRICT	ds_horarios_w
	from	nut_servico_horario
	where	nr_sequencia = nr_seq_horario_w;
else
	ds_horarios_w	:= to_char(clock_timestamp(), 'hh24:mi');
end if;

return	ds_horarios_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
