SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_horario_formatado (qt_horas_p bigint) RETURNS varchar AS $body$
DECLARE

qt_minutos_w		double precision;
qt_horas_w		double precision;
qt_min_w		double precision;
qt_hora_w		double precision := 00;


BEGIN
qt_horas_w		:= qt_horas_p;
qt_minutos_w 		:= (qt_horas_p - qt_horas_w) * 100;

qt_min_w	:= (qt_horas_w * 60) + qt_minutos_w;
while	qt_min_w	> 59 loop
	qt_hora_w	:= qt_hora_w + 1;
	qt_min_w	:= qt_min_w - 60;
end loop;

return lpad(qt_hora_w,2,0)||':'||lpad(trunc(qt_min_w,0),2,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
