CREATE OR REPLACE FUNCTION obter_horario_graf ( ie_hora_p bigint, qt_soma_p bigint) RETURNS bigint AS $body$
DECLARE

ie_hora_w 	bigint;
qt_soma_w	bigint;

BEGIN
qt_soma_w := 1;
ie_hora_w := ie_hora_p;

while(qt_soma_w <= qt_soma_p) loop

	ie_hora_w := ie_hora_w + 1;

	if (ie_hora_w = 24) then
	   ie_hora_w := 0;
	end if;
	qt_soma_w := qt_soma_w + 1;
end loop;

return	ie_hora_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
