CREATE OR REPLACE FUNCTION obter_horario_item_ageint ( nr_seq_agenda_cons_p bigint, nr_seq_agenda_exame_p bigint, nr_seq_item_p bigint) RETURNS timestamp AS $body$
DECLARE


ds_Retorno_w	timestamp;


BEGIN

if (nr_seq_Agenda_cons_p IS NOT NULL AND nr_seq_Agenda_cons_p::text <> '') then
	select	max(dt_Agenda)
	into STRICT	ds_Retorno_w
	from	agenda_consulta
	where	nr_sequencia	= nr_seq_Agenda_cons_p;
elsif (nr_seq_Agenda_exame_p IS NOT NULL AND nr_seq_Agenda_exame_p::text <> '') then
	select	max(hr_inicio)
	into STRICT	ds_Retorno_w
	from	agenda_paciente
	where	nr_sequencia	= nr_seq_Agenda_exame_p;
else
	select 	max(dt_agenda)
	into STRICT	ds_retorno_w
	from	agenda_quimio
	where	nr_seq_ageint_item	= nr_seq_item_p;
end if;

return	ds_Retorno_w;

end;
$body$
