CREATE OR REPLACE FUNCTION obter_horario_prescr_proc_hor ( nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_horario_w  		      timestamp;
nr_seq_origem_w               integer;



BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

	--Verifica se é o exame que gerou os outros horários
	select 	MAX(nr_seq_origem)
	into STRICT 	nr_seq_origem_w
	from	prescr_procedimento
	where 	nr_prescricao = nr_prescricao_p
	and	nr_sequencia  = nr_seq_prescr_p
	and	(ds_horarios IS NOT NULL AND ds_horarios::text <> '');

	-- Se for o exame que gerou os horário, pega o menor horário gerado na prescr_proc_hor
	if (coalesce(nr_seq_origem_w::text, '') = '') then
		select  min(dt_horario)
		into STRICT	dt_horario_w
		from    prescr_proc_hor
		where   nr_prescricao   	= nr_prescricao_p
		and     nr_seq_procedimento	= nr_seq_prescr_p
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';
	end if;

	if (coalesce(dt_horario_w::text, '') = '') then
		select  max(dt_horario)
		into STRICT	dt_horario_w
		from    prescr_proc_hor
		where   nr_prescricao   	= nr_prescricao_p
		and     nr_seq_procedimento	= nr_seq_prescr_p
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

		if (coalesce(dt_horario_w::text, '') = '') then
			select  max(dt_horario)
			into STRICT	dt_horario_w
			from    prescr_proc_hor
			where   nr_prescricao   	= nr_prescricao_p
			and     nr_seq_proc_origem	= nr_seq_prescr_p
			and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';
		end if;
	end if;
end if;

return  dt_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
