CREATE OR REPLACE FUNCTION obter_horario_proced_rotina ( ds_horarios_p text, dt_inicio_prevista_p timestamp ) RETURNS timestamp AS $body$
DECLARE


ds_pri_hor_w	varchar(10);
ds_horario_w	timestamp;


BEGIN

if (ds_horarios_p <> 'SN') and (ds_horarios_p <> 'ACM') and (ds_horarios_p IS NOT NULL AND ds_horarios_p::text <> '')and (dt_inicio_prevista_p IS NOT NULL AND dt_inicio_prevista_p::text <> '')then
	begin
		ds_pri_hor_w	:= substr(ds_horarios_p || ' ',1,position(' ' in ds_horarios_p || ' '));
		ds_horario_w	:= to_date(to_char(dt_inicio_prevista_p,'dd/mm/yyyy') || ' ' || ds_pri_hor_w,'dd/mm/yyyy hh24:mi:ss');


		if (ds_horario_w < clock_timestamp()) then
			ds_horario_w	:= ds_horario_w + 1;
		end if;
	end;
end if;

return	ds_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
