CREATE OR REPLACE FUNCTION obter_horario_qtd_glicose ( nr_prescricao_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_resultado_w	varchar(4000);
ds_horarios_w	varchar(100);

C01 CURSOR FOR
	SELECT	b.ds_horario||' - '||coalesce(qt_glicemia,0)||' mg%'||' /'
	FROM prescr_proc_hor b
LEFT OUTER JOIN atendimento_glicemia c ON (b.nr_sequencia = c.nr_seq_horario)
WHERE b.nr_prescricao		 = nr_prescricao_p and b.dt_horario between dt_inicial_p and dt_final_p order by b.nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	ds_horarios_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ds_horarios_w IS NOT NULL AND ds_horarios_w::text <> '') then
		begin
		ds_resultado_w	:=	ds_resultado_w||' '||ds_horarios_w;
		end;
	end if;
	end;
end loop;
close C01;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
