CREATE OR REPLACE FUNCTION obter_horas_ativ_rat ( nr_seq_atividade_p bigint, nr_seq_rat_p bigint ) RETURNS bigint AS $body$
DECLARE


qt_horas_w 	bigint;


BEGIN

select 	sum(a.qt_min_ativ)/60
into STRICT 	qt_horas_w
from	proj_rat_ativ a,
	proj_cron_etapa b,
	proj_rat c
where 	a.nr_seq_etapa_cron = b.nr_sequencia
and	a.nr_seq_rat = c.nr_sequencia
and	c.nr_sequencia = nr_seq_rat_p
and	b.nr_sequencia = nr_seq_atividade_p;

return	qt_horas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
